var x;

/**
 * 每隔一段时间间隔便会自动执行该函数
 */
function update() {
  if (x <= 2.7) {
      this.position.x += 0.05
    x = x + 0.05;
  } else if (x <= 4.8) {
      this.position.z += (-0.05)
    x = x + 0.05;
  } else {
    x = 0;
      this.position.x += (-2.75)
      this.position.z += 2.1
  }
}


x = 0;
