var ball, event, i, direction, j, pandle, delta, clone, raycaster, score, block, group, objects, object;

/**
 * 描述该功能...
 */
function update(event) {
  if (score >= 5) {
    delta = 70;
  }
  if (score >= 10) {
    delta = 60;
  }
  if (score >= 20) {
    delta = 40;
  }
  if ((ball.position.x) <= -15 || (ball.position.x) >= 15) {
    direction.x = -direction.x
  }
  if ((ball.position.z) <= -15 || (ball.position.z) >= 15) {
    direction.z = -direction.z
  }
  ball.position.x = (Math.min(Math.max((ball.position.x), -15), 15))
  ball.position.z = (Math.min(Math.max((ball.position.z), -15), 15))
  if ((ball.position.z) >= 15) {
    window.alert(String('游戏结束,您输了,您的分数为') + String(score));
    editor.signals.stopPlayer.dispatch()
  }
  var speed = new THREE.Vector3()
    ball.position.add(speed.copy(direction).multiplyScalar(event.delta / delta))
  raycaster.set(ball.position, direction)
  objects = ((function () {
      return raycaster.intersectObjects(group.children)
    })());
  if (objects.length > 0) {
    object = objects[0];
    if ((object.distance) <= 0.5) {
      if ((object.object) != pandle) {
        var voice = editor.voices['ball.mp3'].url
        _audio.src = voice
        _audio.currentTime = 0
        _audio.play()
        group.remove(object.object)

        score = score + 1;
      }
      direction.z = -direction.z
    }
  }
}


ball = (this.getObjectByName('ball'));
direction = ((function () {
    var direction = new THREE.Vector3();

    direction.x = (Math.random() - 0.5)
    direction.y = 0
    direction.z = 0.5

    direction.normalize()

    return direction
  })());
pandle = (this.getObjectByName('pandle'));
raycaster = (new THREE.Raycaster() );
group = ((function (self) {
  var group = new THREE.Group()
  self.add(group)

  return group
})(this) );
group.add(pandle)

block = (this.getObjectByName('block'));
group.add(block)

score = 0;
delta = 80;

for (i = 0; i <= 3; i++) {
  for (j = 0; j <= 9; j++) {
    clone = (block.clone());
    clone.position.x = ((j * 2.7 + 2.85) - 15)
    clone.position.z = ((i * 3 + 5) - 15)
    group.add(clone)

  }
}

