var event, interval;

/**
 * 每隔一段时间间隔便会自动执行该函数
 */
function keydown(event) {
  if (event.keyCode === 37) {
    if ((this.position.x) <= -13) {
      this.position.x = (-13)
    } else {
        this.position.x += (-1)
    }
  }
  if (event.keyCode === 39) {
    if ((this.position.x) >= 13) {
      this.position.x = 13
    } else {
        this.position.x += 1
    }
  }
  clearInterval(interval)
  interval = (setInterval(() => {
      if (event.keyCode === 37) {
      if ((this.position.x) <= -13) {
        this.position.x = (-13)
      } else {
          this.position.x += (-1)
      }
    }
    if (event.keyCode === 39) {
      if ((this.position.x) >= 13) {
        this.position.x = 13
      } else {
          this.position.x += 1
      }
    }

  }, 40));
}

/**
 * 描述该功能...
 */
function keyup(event) {
  clearInterval(interval)
}

